$txt['manual_loginout_always'] = 'Immer eingeloggt bleiben';
$txt['manual_loginout_forgot_password'] = 'Passwort vergessen?';
$txt['manual_loginout_please'] = 'Bitte';
$txt['manual_loginout_login_lower'] = 'Login';
